package JavaLesson6Activities;

public class Laguitan_L6Activity1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0; i < 6; i++) {
			printLines();
		}
	}
	
	/*
	 * Function Name: printLines
	 * 
	 * Inside the function:
	 * 	1. Prints the lines
	 * 			I will not copy and paste code.
	 * 			I will use Camel Case when writing class names.
	 * 			I will use lower Camel Case when writing function names.
	 * 			I will use lower Camel Case when writing variables names.
	 * 
	 * Returns:
	 * 	1. void
	 * */
	public static void printLines() {
		System.out.println(
				"I will not copy and paste code." +
				"\nI will use Camel Case when writing class names." +
				"\nI will use lower Camel Case when writing function names." +
				"\nI will use lower Camel Case when writing variables names.\n"
		);
		
		return;
	}

}
